mapper.insert:
INSERT INTO answer (
  content,
  answer_for,
  created_by
)
VALUES (
  #{content},
  #{answerFor},
  #{createdBy.id}
)

mapper.select:
SELECT value
FROM vote_for_answer
WHERE vote_for = #{id}
  AND created_by = #{userId}

mapper.insert:
INSERT INTO vote_for_answer (
  vote_for,
  created_by,
  value
)
VALUES (
  #{id},
  #{userId},
  #{value}
)
ON DUPLICATE KEY UPDATE
  value = #{value}
